import { Router } from "@angular/router";
import { AuthService } from "./../../services/auth.service";
import { Component } from "@angular/core";

@Component({
  selector: "app-login-form",
  templateUrl: "./login-form.component.html",
  styleUrls: ["./login-form.component.css"],
})
export class LoginFormComponent {
  email: string = "";
  password: string = "";

  constructor(private authService: AuthService, private router: Router) {}

  handleLogin() {
    const regExpEmail = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/;
    const regExpPassword = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9]).{8,12}$/;
    const userObject = {
      email: this.email,
      password: this.password
    }
    if(regExpEmail.test(this.email) && regExpPassword.test(this.password)){
      this.authService.login(userObject).subscribe({
        next: (response) => {
            if(response){
                this.router.navigate(['products'])
            }
        },
        error: () => {

        }
      })
    }
  }
}
<form (ngSubmit)="handleLogin()">
  <div class="form-group">
    <label for="email">Usuario:</label>
    <input type="text" id="email" name="email" [(ngModel)]="email" required>
  </div>
  <div class="form-group">
    <label for="password">Contraseña:</label>
    <input type="password" id="password" name="password" [(ngModel)]="password" required>
  </div>
  <button type="submit">Iniciar sesión</button>
</form>
